# Basic Syntaxprint("Hello, World!")# Variables and Data Typesname = "John"age = 25height = 1.75is_student = False# Displaying Variablesprint("Name:", name)print("Age:", age)print("Height:", height)print("Is Student?", is_student)# basic_operations.pya = 5b= 3.2result = a + bprint(result)# control_flow.py# If Statementnumber = 15if number > 10:    print("The number is greater than 10")else:    print("The number is 10 or less")# For Loopprint("Printing numbers from 1 to 5:")for i in range(1, 6):    print(i)# conditional_loop.pynumber = 7if number % 2 == 0:    print("Even")else:    print("Odd")    for i in range(5):    print(i)    import my_moduleresult = my_module.greet("Eric")print(result)