# dictionaries.py# Creating a dictionaryperson = {    "name": "John",    "age": 30,    "city": "New York"}# Displaying the dictionaryprint("Person:", person)print("Name:", person["name"])print("Age:", person["age"])person["age"] = 31print("Updated person:", person)# Adding new key-value pairs to a dictionaryperson["occupation"] = "Engineer"print("Updated person:", person)# Looping through a dictionaryprint("Printing key-value pairs:")for key, value in person.items():    print(f"{key}: {value}")    # Creating a dictionary for a bookbook = {    "title": "The Hitchhiker's Guide to the Galaxy",    "author": "Douglas Adams",    "publication_year": 1979}# Displaying information about the bookprint("Book Information:")for key, value in book.items():    print(f"{key}: {value}")    # Update the publication year of the bookbook["publication_year"] = 1980print("Updated Book Information:")for key, value in book.items():    print(f"{key}: {value}")