# functions_and_return.py# Function without parametersdef greet():    print("Hello, welcome!")    # Function with parameters and return statementdef add_numbers(a, b):    sum_result = a + b    return sum_result# Function with default parameter valuedef mutiply_numbers(x, y=2):    product_result = x * y    return product_resultgreet()result_sum = add_numbers(4, 6)print("Sum:", result_sum)result_product = mutiply_numbers(4)print("Product:", result_product)